import logoHeader from "../images/logo.svg";
import React from 'react';
import { Switch, Route, Link } from 'react-router-dom';
import burgerImg from "../images/burger.svg"


function Header({loggedIn, email, onSignOut}) {

  const [burgerMenu, setBurgerMenu] = React.useState(false);

  function toggleBurger() {
    setBurgerMenu(true)
  }

  return (
    <header className="header">
      <img
        src={logoHeader}
        alt="логотип проекта Место"
        className="header__logo"
      />
      <nav>
        <ul className="header__nav">
          <Switch>
            <img src={burgerImg} alt="иконка меню"
            className={!burgerMenu ? " header header_burger" : ""}
            onClick={toggleBurger} />
            <Route path="/sign-up">
            <li className="header__item"><Link to="/sign-in"
            className="header__link">Войти</Link></li>
            </Route>
            <Route path="/sign-in">
              <li className="header__item"><Link to="/sign-up" className="header__link">Регистрация</Link></li>
            </Route>
            {loggedIn ? <>
            <li className="header__item">{email}</li>
            <li onClick={onSignOut} className="header__link header__link_out">Выйти</li>
            </> : ''}
          </Switch>
        </ul>
      </nav>
    </header>
  );
}

export default Header;
