import closePopupPath from '../images/close-icon.svg'

function PopupWithForm(props) {
    return (
       <div>
        <section className={`popup popup_${props.name} ${props.isOpen ? 'popup_opened' : 'popup'}`}>
            <div className="popup__container">
            <button className="popup__close" aria-label="Закрыть">
                <img src={closePopupPath} alt="Закрыть" className="popup__icon" onClick={props.onClosePopup}/>
            </button>
            {/* <h3 className="popup__title">{`${props.title}`}</h3> */}
            {/* <form className="popup__form popup__form_edit" name={`${props.name}`} novalidate> */}
            <form className="popup__form" name={`${props.name}`} noValidate>
                <h3 className="popup__title">{`${props.title}`}</h3>
                {props.children}
            </form>
          </div>
        </section>

      </div> 
    )
}

export default PopupWithForm;
