import loadingAvatarPath from '../images/avatar-loading.gif'

function Main() {

    function handleEditAvatarClick() {
        const userAvatarIcon = document.querySelector('.profile__avatar');
        const changeAvatarPopup = document.querySelector('.popup_change-avatar');
        userAvatarIcon.addEventListener('click', () => {
            changeAvatarPopup.classList.add('popup_opened')
        })
    }

    function handleEditProfileClick() {
        const editPopupOpenButton = document.querySelector('.profile__edit-button');
        const editPopup = document.querySelector('.popup_edit');
        editPopupOpenButton.addEventListener('click', () => {
            editPopup.classList.add('popup_opened');
        })
    }

    function handleAddPlaceClick() {
        const addCardBtn = document.querySelector('.profile__add-button');
        const addPopup = document.querySelector('.popup_add');
        addCardBtn.addEventListener('click', () => {
            addPopup.classList.add('popup_opened')
        })
    }

    return (
        <main className="content">

        <section className="profile page__profile">
          <img src={loadingAvatarPath} alt="Жак-Ив Кусто" className="profile__avatar" onClick ={handleEditAvatarClick} />
            <div class="profile__info">
              <h1 className="profile__name">Жак-Ив Кусто</h1>
                <button className="profile__edit-button" aria-label="Открыть" onClick={handleEditProfileClick}></button>
                <p className="profile__profession">Исследователь океана</p>
            </div>
            <button className="profile__add-button" type="button" onCLick={handleAddPlaceClick}></button>
          </section>

        <section className="cards page__cards">
        </section>

        </main>
    )
}

export default Main;
