// import logoPath from '../src/images/logo.svg';
// import closePopupPath from '../images/close-icon.svg'
import Header from './Header';
import Main from './Main';
import Footer from './Footer';
import PopupWithForm from './PopupWithForm';
import ImagePopup from './ImagePopup';
import { useEffect, useState } from 'react';

function App() {

    const [isEditAvatarPopupOpen, setIsEditAvatarPopupOpen] = useState(false);
    const [isEditProfilePopupOpen, setIsEditProfilePopupOpen] = useState(false);
    const [isAddPlacePopupOpen, setIsAddPlacePopupOpen] = useState(false);

    function handleEditAvatarClick() {
      setIsEditAvatarPopupOpen(true)
  };

    function handleEditProfileClick() {
      setIsEditProfilePopupOpen(true)
  };

    function handleAddPlaceClick() {
    setIsAddPlacePopupOpen(true)
  };

    const [onEditAvatarPopupClose, setOnEditAvatarClose] = useState(true);
    const [onEditProfilePopupClose, setOnEditProfilePopupClose] = useState(false);
    const [onAddPlacePopupClose, setOnAddPlacePopupClose] = useState(false);

    function closeAllPopups() {
      setOnEditAvatarClose(!onEditAvatarPopupClose);
      setOnEditProfilePopupClose(!onEditProfilePopupClose);
      setOnAddPlacePopupClose(!onAddPlacePopupClose);
    }


  return (
    <div className="page">
      <Header />
      <Main onEditAvatar={handleEditAvatarClick} onEditProfile={handleEditProfileClick} onAddPlace={handleAddPlaceClick}/>
      <Footer />
      <PopupWithForm title='Редактировать профиль' name='edit' isOpen={isEditProfilePopupOpen} onClose={closeAllPopups}>
        <input 
          id="name-input"
          className="popup__input popup__input_name"
          type="text"
          name="edit-name"
          minLength="2"
          maxLength="40"
          placeholder="Имя"
          required />
          <span id="name-input-error" className="error" />
        <input
          id="profession-input"
          type="text"
          name="edit-profession"
          className="popup__input popup__input_profession"
          minLength="2"
          maxLength="200"
          placeholder="Занятие"
          required />
          <span id="profession-input-error" className="error" />
        <button type="submit" className="popup__submit">Сохранить</button>
      </PopupWithForm>

      <PopupWithForm title='Новое место' name='add' isOpen={isAddPlacePopupOpen} onClose={closeAllPopups}>
        <input
          id="place-input"
          minLength="2"
          maxLength="30"
          type="text"
          name="place-name"
          placeholder="Название"
          className="popup__input popup__input_place"
          required />
          <span id="place-input-error" className="error" />
        <input
          id="link-input"
          type="url"
          name="img-link"
          placeholder="Ссылка на картинку"
          className="popup__input popup__input_link"
          required />
          <span id="link-input-error" className="error" />
        <button type="submit" className="popup__submit popup__submit_add">Создать</button>
      </PopupWithForm>

      <PopupWithForm title='Обновить аватар' name='change-avatar' isOpen={isEditAvatarPopupOpen} onClose={closeAllPopups}>
        <input 
          id="link-avatar"
          type="url"
          name="avatar-link"
          placeholder="Ссылка на аватар"
          className="popup__input popup__input_link"
          required />
          <span id="link-avatar-error" className="error"/>
        <button type="submit" className="popup__submit popup__submit_avatar">Сохранить</button>
      </PopupWithForm>

      <PopupWithForm title='Вы уверены?' name='delete-place' isOpen={false}>
        <button type="submit" className="popup__submit popup__submit_delete">Да</button>
      </PopupWithForm>

      <ImagePopup />

    <template className="card-template">
      <article className="card">
        <img src="#" alt="#" className="card__photo" />
        <button className="card__delete" type="button"></button>
        <div className="card__group">
          <h2 className="card__place" />
          <div className ="card__likes">
            <button className="card__like" type="button"></button>
              <p className="card__likes-counter" />
            </div>
        </div>
      </article>
    </template>
   </div>
  );
}

export default App;
