import React, { useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import './styles/Welcome.css'


function Register() {

    const initialData = {
        email: '',
        password: ''
    };

    const [data, setData] = useState(initialData);
    const [message, setMessage] = useState('');
    const history = useHistory();

    // ВНЕСТИ ИЗМЕНЕНИЯ В ИНПУТЫ
    function handleInputChange(evt) {
        const { name, value } = evt.target;
        setData(data => ({
            ...data,
            [name]: value,
        }));
    };

    // ОЧИСТКА ФОРМЫ
    function resetForm() {
        setData(initialData);
        setMessage('')
    }

    function handleSubmit(evt) {
        evt.preventDefault();
        if (!data.email || !data.password) {
            return
        }
        onRegister(data)
        .then(resetForm)
        .then(() => history.push('/sign-in'))
        .catch(err => setMessage(err.message))
    }

  return (
    <div className="welcome welcome_register">
      <form className="welcome__form welcome__form_register">
        <h3 className="welcome__title">Регистрация</h3>
        <input
          id="register-email"
          name="register-email"
          type="email"
          className="welcome__input welcome__input_register_email"
          placeholder="Email"
        ></input>
        <input
          id="register-password"
          name="register-password"
          type="password"
          className="welcome__input welcome__input_register_password"
          placeholder="Пароль"
        ></input>
        <button
          className="welcome__button welcome__button_register"
          type="submit"
        >
          Зарегистрироваться
        </button>
      </form>
      <div className="welcome__signin">
        <p>Уже зарегистрированы?</p>
        <Link to="" className="welcome__login-link">Войти</Link>
      </div>
    </div>
  );
}

export default Register;
