import React from "react";
import Card from "../components/Card";
import api from "../api/api";
import { CurrentUserContext } from "../contexts/CurrentUserContext.js";

function Main(props) {
  const currentUser = React.useContext(CurrentUserContext);
  const [cards, setCards] = React.useState([]);

  function handleCardLike(card) {

    const isLiked = card.likes.some(i => i._id === currentUser._id);
    const likeRequest = !isLiked
    api.putLike(card._id, !isLiked).then((newCard) => {
        // Формируем новый массив на основе имеющегося, подставляя в него новую карточку
      const newCards = props.cards.map((c) => c._id === card._id ? newCard : c);
      // Обновляем стейт
      setCards(newCards);
    });
}

  return (
    <main className="content">
      <section className="profile page__profile">
        <img
          src={currentUser.avatar}
          alt={currentUser.name}
          className="profile__avatar"
          onClick={props.onEditAvatar}
        />
        <div className="profile__info">
          <h1 className="profile__name">{currentUser.name}</h1>
          <button
            className="profile__edit-button"
            aria-label="Открыть"
            onClick={props.onEditProfile}
          ></button>
          <p className="profile__profession">{currentUser.about}</p>
        </div>
        <button
          className="profile__add-button"
          type="button"
          onClick={props.onAddPlace}
        ></button>
      </section>

      <section className="cards page__cards">
        {props.cards.map((card) => {
          return (
            <Card key={card._id} card={card} onCardClick={props.onCardClick} onCardLike={handleCardLike}/>
          );
        })}
      </section>
    </main>
  );
}

export default Main;
