import React from 'react'
import api from '../utils/api.js';

function Main(props) {

    const [userName, setUserName] = React.useState('');
    const [userDescription, setUserDescription] = React.useState('');
    const [userAvatar, setUserAvatar] = React.useState('');
    const [cards, setCards] = React.useState([]);

    React.useEffect(() => {
        api.getUserData()
        .then((user) => {
            setUserName(user.name);
            setUserDescription(user.about);
            setUserAvatar(user.avatar)
        })
        .catch((err) => {
            console.log(err)
        })
    }, []);

    React.useEffect(() => {
        api.getInitialCards()
        .then((cards) => {
            setCards(cards)
        })
        .catch((err) => {
            console.log(err)
        })
    }, []);

    cards.map((card) => {
        return (
            <template className="card-template" key={card._id}>
            <article className="card">
              <img src={card.link} alt={card.name} className="card__photo" />
              <button className="card__delete" type="button"></button>
              <div className="card__group">
                <h2 className="card__place">{card.name}</h2>
                <div className ="card__likes">
                  <button className="card__like" type="button"></button>
                <p className="card__likes-counter">{card.likes.length}</p>
                  </div>
              </div>
            </article>
          </template>
        )
    })

    return (
        <main className="content">

        <section className="profile page__profile">
          <img src={userAvatar} alt="Жак-Ив Кусто" className="profile__avatar" onClick ={props.onEditAvatar} />
            <div className="profile__info">
                <h1 className="profile__name">{userName}</h1>
                <button className="profile__edit-button" aria-label="Открыть" onClick={props.onEditProfile}></button>
                <p className="profile__profession">{userDescription}</p>
            </div>
            <button className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
          </section>

        <section className="cards page__cards">
        </section>

        </main>
    )
}

export default Main;
